let modalOpenBtn=document.querySelector(".dlk-info-btn"),popupOverlay=document.querySelector(".modal-overlay"),modalWindow=document.querySelector(".modal-window"),modalCloseBtn=document.querySelector(".popup-close"),modalInput=modalWindow.querySelector("#contact-name"),sliderToggleBtn=document.querySelectorAll(".slider-toggle-btn"),sliderCounter=document.querySelector(".slider-counter");function removeClassActiveBtn(e){e.forEach(e=>{e.classList.remove("slider-toggle-active")})}modalOpenBtn.addEventListener("click",()=>{popupOverlay.classList.add("popup-visible"),modalWindow.classList.add("popup-visible"),modalInput.focus()}),modalCloseBtn.addEventListener("click",()=>{popupOverlay.classList.remove("popup-visible"),modalWindow.classList.remove("popup-visible")}),window.addEventListener("keydown",e=>{"Escape"===e.key&&(popupOverlay.classList.remove("popup-visible"),modalWindow.classList.remove("popup-visible"))}),sliderToggleBtn.forEach(e=>e.addEventListener(`click`,e=>{sliderCounter.textContent=e.target.dataset.slidenumber,removeClassActiveBtn(sliderToggleBtn),e.target.classList.add("slider-toggle-active")}));